PopoutForm = function(){
    var signup_popup,
        cookie_name = 'subscription_popout',
        first_visited = Mage.Cookies.get(cookie_name), // test for cookie or logged in to stop the prompting
        cookie_expiration = 60*60*1000*24, // 24 hour
        first_visitor = true,
        signup_form,
        popout = {
            speed : 700,
            type : 'ease',
            delay: 3500,
            width: '350px'
        },
        submit_url = '/newsletterSubscribeAPI.php',
        is_submitting = false,

        notification = $('<div>').addClass('popout-mailchimp-notification')
            .css({
                    'opacity': 0,
                    'text-align': 'center',
                    'font-size' : '0.9rem',
                    'position' : 'absolute',
                    'border' : '1px solid #ccc',
                    'bordRadius' : '5px',
                    'backgroundColor' : '#fff',
                    'padding': '10px',
                    'margin': '10px 0 0',
                    'width' : '260px',
                    'border-radius' : '5px'
            }),

    // {{{ isElementInViewport = function(el)
    isElementInViewport = function(el) {
        if (el instanceof jQuery) {
            el = el[0];
        }
        var rect = el.getBoundingClientRect();
        return (
            rect.top >= 0 &&
            rect.left >= 0 &&
            rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
            rect.right <= (window.innerWidth || document.documentElement.clientWidth)
        );
    }
    // }}}
    // {{{ fireIfElementVisible = function(el, callback)
    fireIfElementVisible = function(el, callback) {
        return function () {
            if ( isElementInViewport(el) && first_visitor) {
                first_visitor = false;
                callback();
            }
        }
    },
    // }}} 
    // {{{ notify = function(message)
    notify = function(message){
        notification.html(message);
        notification.transition({opacity: 1}, popout.speed, popout.type,
            function() {
                notification.transition({opacity: 0, delay: popout.delay }, popout.speed, popout.type
                );
            });
    },
    // }}}
    // {{{ showPopup = function()
    showPopup = function() {
        var expiration = new Date();
        signup_popup.transition({x: popout.width, opacity: 1}, popout.speed, popout.type, function() {
            $(this).toggleClass('is_out');
        });
        expiration.setTime(expiration.getTime() + cookie_expiration); // 1 hour
        Mage.Cookies.set(cookie_name, true, expiration);
    },
    // }}}
    bindActions = function(){
        // tab toggler
        $('.signup-tab').click(function() {
            signup_popup.hasClass('is_out')
                ? signup_popup.transition({x: '0px'}, popout.speed, popout.type)
                : signup_popup.transition({x: popout.width}, popout.speed, popout.type);
            signup_popup.toggleClass('is_out');
        });

        signup_form.append(notification);
        // signup form submit
        signup_form.submit(function(e) {
            e.preventDefault();
            if(!is_submitting){
                is_submitting = true;
                var el = $(this),
                    data = {
                        email : $('input[name="EMAIL"]', el).val(),
                        SIGNUP : 'Ecommerce - '+ document.title,
                        CATEGORY : $('input[name="CATEGORY"]', el).val(),
                        SIGNUPURL : window.location.pathname,
                    };
                $.post(submit_url, data, function(data){
                    switch(data){
                        case 'already subscribed':
                            notify('This email is already subscribed<br/>to our mailing list.');
                            break;
                        case 'error':
                            notify('There was an error subscribing.<br/>Please try again.');
                            break;
                        case 'success':
                            notify('You\'ve been successfully subscribed!<br/>Please check your email to verify the subscription.');
                            break;
                    }
                    is_submitting = false;
                });
            }
            return false;
        });

    },

    init = function(handle, trigger){
        if(first_visited === null){
            signup_popup = $(handle);
            signup_form = $('form', signup_popup);
            bindActions();
            handler = fireIfElementVisible($(trigger), showPopup);
            $(window).on('DOMContentLoaded load resize scroll', handler);
        }
    };
    return {
        init: init
    }
}();
