        // {{{ Measurement process stimulus message
	function sliderProgress(isNext){
            var progress_msgs = {
                50 : "You're already halfway done!",
                75: "Only a few more steps left!"
              },
              previous_width = width;
		if(isNext){
			width = width + temp_width;
			//increase progress bar with %
			jQuery('.progress-bar .progress').css({'width':width+'%'});
		} else {
			width = width - temp_width;
			//decrease progress bar with %
			jQuery('.progress-bar .progress').css({'width':width+'%'});
		}
            checkProgress(previous_width, progress_msgs);
	};

        function checkProgress(previous_width, msgs) {
            delete window['console'];
            var msg_wrap = jQuery(".msg-wrap"),
                msg_content = jQuery('.measurement-msg'),
                show_msg = false;
            for(var percent in msgs){
              if (previous_width < percent && width >= percent ||
                previous_width > percent && width <= percent) {
                show_msg = true;
                popup_msg = msgs[percent];
                break;
              }
            }
            if (show_msg) {
              msg_content.html(popup_msg);
              msg_wrap.fadeIn();
            } else {
              msg_wrap.fadeOut( function(){ jQuery(this).html(); });
            }
        }
